import React, { useEffect } from 'react'
import { Card, Image } from 'antd'
import { FaBirthdayCake } from 'react-icons/fa'
import { FiMapPin } from 'react-icons/fi'
import { BsFillTelephoneFill } from 'react-icons/bs'
import { useStateContext } from '../Context/ContextProvider';
import axiosClient from '../Services/axios';


const Welcome = () => {

    const { setUser, user } = useStateContext();
    useEffect(() => {
        axiosClient.get('/user').then(({ data }) => {
            axiosClient.get('/patient/' + data.id).then((userData) => {
                setUser(userData.data);
            })
        })
    }, [])

    return (
        <>
            <div className="container-md mt-3">
                <Card>
                    <div className="row">
                        <div className="d-flex justify-content-around align-items-center flex-wrap ">
                            <div className='col-sm-4 text-center p-2'>
                                <Image
                                    className='rounded-circle'
                                    width={200}
                                    {...!user.profile_photo ?
                                        {
                                            src: "error",
                                            fallback: "data:image/png;base64,",
                                            preview: false
                                        } : {
                                            src: { ...user.profile_photo }
                                        }}
                                />
                            </div>
                            <div className='col-sm-6 border-start px-3'>
                                {/* <h3 className='text-secondary'>Welcome,</h3> */}
                                <p className='text-center fs-1 my-2'>{user.full_name || ""}</p>
                                <p className='text-center fs-5 my-4'>{user.email || ""}</p>
                                <div className="fs-5 d-lg-flex text-center justify-content-around my-4">
                                    <p><FaBirthdayCake className='fs-3 mx-2' />{user.dob || ""}</p>
                                    <p><FiMapPin className='fs-3 mx-2' />{user.address || ""}</p>
                                </div>
                                <p className='text-center fs-5'><BsFillTelephoneFill className='fs-3 mx-2' />{user.phone_number || ""}</p>
                            </div>
                        </div>
                    </div>
                </Card>
            </div>
        </>
    )
}

export default Welcome